package variable.com.eclectic.Chats;import android.content.Intent;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.google.android.material.floatingactionbutton.FloatingActionButton;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;import variable.com.eclectic.R;public class StatusTab extends Fragment implements View.OnClickListener{    private FloatingActionButton fab_status_camera;    private FloatingActionButton fab_status_edit;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View rootView = inflater.inflate(R.layout.tab_status,container,false);        setHasOptionsMenu(true);        fab_status_camera = rootView.findViewById(R.id.fab_status_camera);        fab_status_edit = rootView.findViewById(R.id.fab_status_edit);        fab_status_camera.setOnClickListener(this);        fab_status_edit.setOnClickListener(this);        return rootView;    }    @Override    public void onClick(View v) {        switch(v.getId())        {            case R.id.fab_status_edit:            {                Intent intent = new Intent(getActivity(),Chat_WriteStatus.class);                startActivity(intent);            }            break;            case R.id.fab_status_camera:            {            }            break;        }    }}